package com.looke.avplayback.core.diimport android.app.Applicationimport com.looke.avplayback.core.application.CustomApplicationimport com.looke.avplayback.videos.di.VideosModuleimport dagger.BindsInstanceimport dagger.Componentimport dagger.android.*import dagger.android.support.AndroidSupportInjectionModuleimport javax.inject.Singleton@Singleton@Component(    modules = [        AndroidInjectionModule::class,        BaseModule::class,        VideosModule::class    ])interface ApplicationComponent: AndroidInjector<DaggerApplication> {    @Component.Builder    interface Builder {        fun build(): ApplicationComponent        @BindsInstance        fun applicationBind(application: Application): Builder    }    fun inject(customApplication: CustomApplication)}